import React, { useEffect, useState } from "react";
import "./SoloGame.css";
import wordList from "../data/wordList.json";

const MAX_ATTEMPTS = 6;
const WORD_LENGTH = 5;
const STARTING_HP = 100;
const STARTING_TIME = 60;

function generateWord(wordList) {
  return wordList[Math.floor(Math.random() * wordList.length)].toUpperCase();
}

export default function SoloGame({ selectedWizard, username }) {
  const [board, setBoard] = useState(Array(MAX_ATTEMPTS).fill("").map(() => Array(WORD_LENGTH).fill("")));
  const [colors, setColors] = useState(Array(MAX_ATTEMPTS).fill("").map(() => Array(WORD_LENGTH).fill("")));
  const [currentAttempt, setCurrentAttempt] = useState(0);
  const [currentLetterIndex, setCurrentLetterIndex] = useState(0);
  const [targetWord, setTargetWord] = useState(generateWord(wordList));
  const [hp, setHP] = useState(STARTING_HP);
  const [timer, setTimer] = useState(STARTING_TIME);
  const [message, setMessage] = useState("");
  const [gameOver, setGameOver] = useState(false);

  const handleFizzle = () => {
    setMessage("Your spell fizzled!");
    setHP((prev) => prev - 20);
    setTimeout(() => resetRound(false), 2000);
  };
  
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (gameOver) return;

      const key = e.key.toUpperCase();
      if (key === "BACKSPACE") {
        handleBackspace();
      } else if (key === "ENTER") {
        handleSubmit();
      } else if (/^[A-Z]$/.test(key) && currentLetterIndex < WORD_LENGTH) {
        handleLetterInput(key);
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  });

  useEffect(() => {
    if (gameOver) return;
    if (timer <= 0) {
      handleFizzle();
      return;
    }

    const interval = setInterval(() => {
      setTimer((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(interval);
  }, [timer, gameOver, handleFizzle]);

  const handleLetterInput = (letter) => {
    const newBoard = [...board];
    newBoard[currentAttempt][currentLetterIndex] = letter;
    setBoard(newBoard);
    setCurrentLetterIndex((prev) => prev + 1);
  };

  const handleBackspace = () => {
    if (currentLetterIndex === 0) return;
    const newBoard = [...board];
    newBoard[currentAttempt][currentLetterIndex - 1] = "";
    setBoard(newBoard);
    setCurrentLetterIndex((prev) => prev - 1);
  };

  const handleSubmit = () => {
    const guess = board[currentAttempt].join("");
    if (guess.length < WORD_LENGTH) {
      setMessage("Not enough letters!");
      return;
    }

    const newColors = Array(WORD_LENGTH).fill("gray");
    const targetLetters = targetWord.split("");

    // First pass for correct letters
    for (let i = 0; i < WORD_LENGTH; i++) {
      if (guess[i] === targetWord[i]) {
        newColors[i] = "green";
        targetLetters[i] = null;
      }
    }

    // Second pass for present but wrong position
    for (let i = 0; i < WORD_LENGTH; i++) {
      if (newColors[i] === "green") continue;
      const index = targetLetters.indexOf(guess[i]);
      if (index !== -1) {
        newColors[i] = "yellow";
        targetLetters[index] = null;
      }
    }

    const newColorBoard = [...colors];
    newColorBoard[currentAttempt] = newColors;
    setColors(newColorBoard);

    if (guess === targetWord) {
      setMessage("Spell Cast! ðŸ”¥");
      setTimeout(() => resetRound(true), 1000);
    } else if (currentAttempt + 1 === MAX_ATTEMPTS) {
      handleFizzle();
    } else {
      setCurrentAttempt((prev) => prev + 1);
      setCurrentLetterIndex(0);
    }
  };


  const resetRound = (success) => {
    if (hp <= 20 && !success) {
      setGameOver(true);
      setMessage("Game Over!");
      return;
    }
    setTimer(STARTING_TIME);
    setCurrentAttempt(0);
    setCurrentLetterIndex(0);
    setBoard(Array(MAX_ATTEMPTS).fill("").map(() => Array(WORD_LENGTH).fill("")));
    setColors(Array(MAX_ATTEMPTS).fill("").map(() => Array(WORD_LENGTH).fill("")));
    setTargetWord(generateWord(wordList));
    setMessage("");
  };


  return (
    <div className="min-h-screen flex items-center justify-center bg-[#121212] text-white">
      <div className="border-4 border-gray-500 rounded-2xl p-6 w-full max-w-xl text-center">
        {/* Player Info */}
        <div className="mb-4">
          <h2 className="text-3xl font-bold">{username}</h2>
          <p className="text-lg">HP: {hp}</p>
        </div>

        {/* Wizard Image */}
        {wizard?.img && (
          <div className="flex justify-center mb-4">
            <img src={wizard.img} alt={wizard.name} className="h-32 rounded" />
          </div>
        )}

        {/* Progress Bar */}
        <div className="w-full bg-gray-700 rounded-full h-4 mb-6">
          <div
            className="bg-red-500 h-4 rounded-full transition-all duration-1000"
            style={{ width: `${(timeLeft / 60) * 100}%` }}
          />
        </div>

        {/* Wordle Grid */}
        <div className="grid grid-rows-6 gap-2 justify-center">
          {[...Array(6)].map((_, rowIndex) => {
            const guess = guesses[rowIndex] || (rowIndex === guesses.length ? currentGuess : '');
            return (
              <div key={rowIndex} className="grid grid-cols-5 gap-2">
                {[...Array(5)].map((_, colIndex) => {
                  const letter = guess[colIndex] || '';
                  return renderTile(letter, colIndex, guess);
                })}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};
