import React, { useState } from "react";

const wizards = [
  {
    id: "fire",
    name: "Fire Mage",
    img: "./images/fire-mage.png",
    ability: "Extra Damage",
    description: "Deals extra damage when a spell is successfully cast."
  },
  {
    id: "ice",
    name: "Ice Mage",
    img: "./images/ice-mage.png",
    ability: "Locks a Row",
    description: "Locks a row for the opponent when a spell is cast."
  },
  {
    id: "nature",
    name: "Nature Mage",
    img: "./images/nature-mage.png",
    ability: "Heal HP",
    description: "Heals HP based on leftover tries after casting a spell."
  }
];

export default function MainMenu() {
  const [selectedWizard, setSelectedWizard] = useState(wizards[0]);
  const [username, setUsername] = useState("");

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-800 to-indigo-900 text-white p-6">
      <h1 className="text-4xl font-bold text-center mb-10">SpellWord: Wizard Duels</h1>

      <div className="grid grid-cols-3 gap-6">
        {/* How to Play Panel */}
        <div className="bg-white bg-opacity-10 p-4 rounded-xl shadow-lg">
          <h2 className="text-2xl font-semibold mb-3">How to Play</h2>
          <ul className="list-disc pl-5 space-y-2">
            <li>Guess the word to cast a spell.</li>
            <li>Choose your wizard to gain special powers.</li>
            <li>Inflict damage based on leftover tries and speed.</li>
            <li>First to drop to 0 HP loses!</li>
          </ul>
        </div>

        {/* Username Input and Play Button */}
        <div className="bg-white bg-opacity-10 p-4 rounded-xl shadow-lg flex flex-col items-center justify-center">
          <input
            type="text"
            placeholder="Enter your username"
            className="p-2 rounded text-black mb-4 w-full"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <button className="bg-yellow-400 hover:bg-yellow-500 text-black font-bold py-2 px-4 rounded">
            Play with Friend
          </button>
        </div>

        {/* Character Selection */}
        <div className="bg-white bg-opacity-10 p-4 rounded-xl shadow-lg">
          <h2 className="text-2xl font-semibold mb-3">Select Your Wizard</h2>
          <div className="flex justify-between space-x-4 mb-4">
            {wizards.map((wizard) => (
              <div
                key={wizard.id}
                onClick={() => setSelectedWizard(wizard)}
                className={`cursor-pointer p-2 rounded-xl border-2 transition duration-300 ${
                  selectedWizard.id === wizard.id ? "border-yellow-400" : "border-transparent"
                }`}
              >
                <img
                  src={wizard.img}
                  alt={wizard.name}
                  className="w-24 h-24 object-contain"
                />
                <p className="text-center mt-2">{wizard.name}</p>
              </div>
            ))}
          </div>

          {/* Wizard Summary */}
          <div className="bg-white bg-opacity-10 p-4 rounded-md">
            <h3 className="text-xl font-bold mb-1">{selectedWizard.name}</h3>
            <p className="italic mb-1">Ability: {selectedWizard.ability}</p>
            <p>{selectedWizard.description}</p>
          </div>
        </div>
      </div>
    </div>
  );
}
